import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [count, setCount] = useState(() => {
    const savedCount = localStorage.getItem("count");
    return savedCount !== null ? parseInt(savedCount, 10) : 0;
  });

  const [countTrue, setCountTrue] = useState(10);
  const [progressBar, setProgressBar] = useState(countTrue / 10);
  useEffect(() => {
    localStorage.setItem("count", count.toString());

    let interval;

    // Запускаем interval, если countTrue < 1000
    if (countTrue < 1000) {
      interval = setInterval(() => {
        setCountTrue((prevCount) => {
          // Увеличиваем prevCount, пока он не достигнет 1000
          if (prevCount < 1000) {
            return prevCount + 1;

          } else {
            clearInterval(interval);
            return prevCount;
          }
        });
      }, 5000);
    }
    //nen
    return () => clearInterval(interval);
  }, [count, countTrue]);

  const handleClick = () => {
    setCount(count + 1);

    if (countTrue > 0) {
      setCountTrue(countTrue - 1);
      setProgressBar()
    }
  };

  return (
    <div className="App">
      <h2 className="title">Quack Coin</h2>
      <p className="count-click">{count}</p>
      <div className="coin-btn">
        <button className="coin-button" onClick={handleClick}>
          ...
        </button>
      </div>
      <hr className="coin-progress" style={{ width: progressBar }} />
      {countTrue}
    </div>
  );
}

export default App;
