import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [count, setCount] = useState(() => {
    const savedCount = localStorage.getItem("count");
    return savedCount !== null ? parseInt(savedCount, 10) : 0;
  });

  const [countTrue, setCountTrue] = useState(10);

  useEffect(() => {
    localStorage.setItem("count", count.toString());

    let interval;

    if (countTrue === 0 && countTrue < 10) {
      alert("Скрудж устал 😴");

      interval = setInterval(() => {
        setCountTrue((prevCount) => {
          if (prevCount < 10) {
            return prevCount + 1;
          } else {
            clearInterval(interval);
            return prevCount;
          }
        });
      }, 30);
    }

    return () => clearInterval(interval);
  }, [count, countTrue]);

  const handleClick = () => {
    setCount(count + 1);

    // Уменьшаем countTrue только если он больше 0
    if (countTrue > 0) {
      setCountTrue(countTrue - 1);
    }
  };

  return (
    <div className="App">
      <h2 className="title">Quack Coin</h2>
      <p className="count-click">{count}</p>
      <div className="coin-btn">
        <button className="coin-button" onClick={handleClick}>
          ...
        </button>
      </div>
      <hr className="coin-progress" style={{ width: countTrue }} />
      {countTrue}
    </div>
  );
}

export default App;
